mapper.insert:
INSERT INTO banner(
    id
    , title
    , exposure_location
    , image_file_group_id
    , url
    , ord
    , is_enabled
    , created_at
    , created_by
    , modified_at
    , modified_by
)VALUES (
    #{id}
    , #{title}
    , #{exposureLocation}
    , #{imageFileGroupId}
    , #{url}
    , #{ord}
    , #{isEnabled}
    , NOW()
    , #{member.id}
    , NOW()
    , #{member.id}
)

mapper.update:
UPDATE banner
SET
    title                   = #{title}
    , exposure_location     = #{exposureLocation}
    , image_file_group_id   = #{imageFileGroupId}
    , is_enabled            = #{isEnabled}
    , modified_at           = NOW()
    , modified_by           = #{member.id}
WHERE
    id = #{id}

UPDATE banner
SET
    ord           = #{ord}
    , is_enabled  = #{isEnabled}
    , modified_at = NOW()
    , modified_by = #{member.id}
WHERE
    id = #{id}

update.foreach:
UPDATE banner SET
    ORD             = #{item.ord}
    , modified_at   = NOW()
    , modified_by   = #{member.id}
WHERE
    id = #{item.id}
