mapper.select:
SELECT A.ID , A.CATEGORY , A.TITLE , A.HITS , B.NICKNAME , A.CREATED_BY , A.CREATED_AT FROM POST A LEFT OUTER JOIN MEMBER B ON A.CREATED_BY = B.ID INNER JOIN board C ON A.board_id = C.id WHERE C.category = 'ADMIN' AND A.IS_DELETED = 'N'
AND B.EMAIL LIKE CONCAT('%',#{searchKeyword},'%')
AND B.NICKNAME LIKE CONCAT('%',#{searchKeyword},'%')
AND A.CATEGORY = #{searchCategory}
AND A.TITLE LIKE CONCAT('%',#{searchTitle},'%')
AND DATE(A.CREATED_AT) >= #{searchBgngDt}
AND DATE(A.CREATED_AT) <= #{searchEndDt}

mapper.insert:
INSERT INTO POST(
      board_id
    , category
    , title
    , content
    , modified_at
    , modified_by
    , created_at
    , created_by
)VALUES (
    2
    , #{category}
    , #{title}
    , #{content}
    , NOW()
    , #{member.id}
    , NOW()
    , #{member.id}
)

mapper.update:
UPDATE POST
SET
  category    = #{category}
  , title       = #{title}
  , content     = #{content}
  , modified_at = NOW()
  , modified_by = #{member.id}
WHERE
    id = #{id}
