) , recordsTotal AS ( SELECT COUNT(1) AS recordsTotal FROM SEARCH_ORG A1 ) SELECT A1.*,
B1.recordsTotal - (ROW_NUMBER() OVER(ORDER BY A1.${orderField} DESC)) + 1 AS rowNumber,
B1.recordsTotal - (ROW_NUMBER() OVER(ORDER BY A1.${orderField} ASC)) + 1 AS rowNumber,
B1.recordsTotal - (ROW_NUMBER() OVER(ORDER BY A1.${orderField})) + 1 AS rowNumber,
B1.recordsTotal , IFNULL(#{pageNumber}, 1) AS pageNumber , CEILING(CONVERT(B1.recordsTotal, FLOAT) / IFNULL(#{length}, 10)) AS totalPage , #{length} AS pageSize FROM SEARCH_ORG A1 CROSS JOIN recordsTotal B1 ORDER BY
A1.${orderField} DESC
A1.${orderField} ASC
A1.${orderField}
LIMIT #{start}, #{length}
