mapper.select:
SELECT id , title , image_file_group_id , ord , is_enabled FROM popup WHERE 1 = 1
AND title = #{searchTitle}
AND exposure_location = #{searchExposureLocation}
ORDER BY ord ASC NULLS LAST

mapper.insert:
INSERT INTO popup(
        title
        , exposure_location
        , target
        , target_role_id
        , start_date
        , end_date
        , image_file_group_id
        , url
        , button_option
        , ord
        , is_enabled
        , created_at
        , created_by
        , modified_at
        , modified_by
)VALUES (
        #{title}
        , #{exposureLocation}
        , #{target}
        , #{targetRoleId}
        , #{startDate}
        , #{endDate}
        , #{imageFileGroupId}
        , #{url}
        , #{buttonOption}
        , #{ord}
        , #{isEnabled}
        , NOW()
        , #{member.id}
        , NOW()
        , #{member.id}
        )

mapper.update:
UPDATE popup
SET
    title                   = #{title}
  , exposure_location       = #{exposureLocation}
  , target                  = #{target}
  , target_role_id          = #{targetRoleId}
  , start_date              = #{startDate}
  , end_date                = #{endDate}
  , image_file_group_id     = #{imageFileGroupId}
  , url                     = #{url}
  , button_option           = #{buttonOption}
  , ord                     = #{ord}
  , is_enabled              = #{isEnabled}
  , modified_at             = NOW()
  , modified_by             = #{member.id}
WHERE
    id = #{id}

UPDATE popup
SET
    ord           = #{ord}
  , is_enabled  = #{isEnabled}
  , modified_at = NOW()
  , modified_by = #{member.id}
WHERE
    id = #{id}

update.foreach:
UPDATE popup SET
ORD             = #{item.ord}
, modified_at   = NOW()
, modified_by   = #{member.id}
WHERE
id = #{item.id}
