mapper.select:
SELECT id , method , title , time_point , category , is_enabled , created_at FROM SEND_TEMPLATE WHERE 1 = 1
AND method = #{searchMethod}
AND category = #{searchCategory}
AND title LIKE CONCAT('%',#{searchTitle},'%')
AND is_enabled = #{searchIsEnabled}

mapper.insert:
INSERT INTO send_template(
    method
    , title
    , content
    , category
    , is_enabled
    , time_point
    , created_at
    , created_by
    , modified_at
    , modified_by
)VALUES (
    #{method}
    , #{title}
    , #{content}
    , #{category}
    , #{isEnabled}
    , '-'
    , NOW()
    , #{member.id}
    , NOW()
    , #{member.id}
)

mapper.update:
UPDATE send_template
SET
    method      = #{method}
  , title       = #{title}
  , content     = #{content}
  , category    = #{category}
  , is_enabled  = #{isEnabled}
  , modified_at = NOW()
  , modified_by = #{member.id}
WHERE
    id = #{id}

mapper.delete:
DELETE FROM send_template
WHERE id IN

    #{item}
